'use strict';

module.exports = function (router) {

  /**
   * Define all front-end routes here.
   */
  router.get([
    /* Pages */
    '/', '/help', '/about',

    /* Error routes */
    '/lost', '/forbidden',

    /* Users */
    '/users/sign-up', '/users/sign-in', '/users/recover'

  ], function (req, res) {
    /* Set the XSRF-TOKEN cookie on first request (Generated by lusca) */
    res.cookie('XSRF-TOKEN', res.locals._csrf);

    /* Render the base layout */
    res.render('index', {
      development: process.env.NODE_ENV === 'development',
      title: require(__basedir + '/../package.json').title
    });

  });

};
